package com.cyber.authing.entity.request;import com.alibaba.fastjson.JSONArray;import com.cyber.authing.entity.domain.AccessConfig;import com.cyber.domain.entity.PagingRequest;import lombok.Data;import lombok.EqualsAndHashCode;import org.springframework.beans.BeanUtils;@EqualsAndHashCode(callSuper = true)@Datapublic class AccessConfigRequest extends PagingRequest {    /**     * 接入系统     */    private String accessSys;    /**     * 接入Key     */    private String accessKey;    /**     * 私钥:加密存储     */    private String privateKey;    /**     * 公钥:加密存储     */    private String publicKey;    /**     * 会话策略配置     */    private JSONArray policyConfig;    public AccessConfig toEvent(String tenantCode) {        AccessConfig accessConfig = new AccessConfig();        BeanUtils.copyProperties(this, accessConfig);        accessConfig.setTenantCode(tenantCode);        return accessConfig;    }}