package com.cyber.authing.entity.request;import com.alibaba.fastjson.JSONArray;import com.cyber.authing.entity.domain.AccessConfig;import com.cyber.domain.entity.OperateEntity;import lombok.Data;import lombok.EqualsAndHashCode;import org.springframework.beans.BeanUtils;import javax.validation.constraints.NotBlank;import java.util.Date;@EqualsAndHashCode(callSuper = true)@Datapublic class CreateAccessConfigRequest extends OperateEntity {    /**     * 接入系统     */    @NotBlank(message = "接入系统不能为空")    private String accessSys;    /**     * 接入Key     */    @NotBlank(message = "接入Key不能为空")    private String accessKey;    /**     * 私钥:加密存储     */    @NotBlank(message = "访问私钥不能为空")    private String privateKey;    /**     * 公钥:加密存储     */    @NotBlank(message = "访问公钥不能为空")    private String publicKey;    /**     * 会话策略配置     */    private JSONArray policyConfig;    public AccessConfig toEvent(String userCode, String tenantCode) {        AccessConfig accessConfig = new AccessConfig();        BeanUtils.copyProperties(this, accessConfig);        accessConfig.setTenantCode(tenantCode);        accessConfig.setCreator(userCode);        accessConfig.setCreateTime(new Date());        accessConfig.setUpdator(userCode);        accessConfig.setUpdateTime(new Date());        return accessConfig;    }}