package com.cyber.authing.entity.request;import com.cyber.authing.entity.domain.Position;import com.cyber.domain.entity.OperateEntity;import lombok.Data;import lombok.EqualsAndHashCode;import org.springframework.beans.BeanUtils;import javax.validation.constraints.NotBlank;import java.util.Date;@EqualsAndHashCode(callSuper = true)@Datapublic class CreatePositionRequest extends OperateEntity {    /**     * 岗位编码     */	@NotBlank    private String code;    /**     * 岗位名称     */	@NotBlank    private String name;    /**     * 显示顺序     */    private Integer orderNum;    /**     * 部门ID     */    private Long deptId;    /**     * 岗位描述     */    private String description;    public Position toEvent(String userCode, String tenantCode) {        Position position = new Position();        BeanUtils.copyProperties(this, position);        position.setTenantCode(tenantCode);        position.setCreator(userCode);        position.setCreateTime(new Date());        position.setUpdator(userCode);        position.setUpdateTime(new Date());        return position;    }}