package com.cyber.authing.entity.request;import com.cyber.authing.entity.domain.Permission;import com.cyber.authing.entity.domain.Role;import com.cyber.domain.entity.OperateEntity;import lombok.Data;import lombok.EqualsAndHashCode;import org.springframework.beans.BeanUtils;import javax.validation.constraints.NotBlank;import java.util.Date;import java.util.List;@EqualsAndHashCode(callSuper = true)@Datapublic class CreateRoleRequest extends OperateEntity {    /**     * 角色编码     */    @NotBlank    private String code;    /**     * 角色名称     */    @NotBlank    private String name;    /**     * 部门ID     */    private Long deptId;    /**     * 岗位ID     */    private Long positionId;    /**     * 产品编码     */    @NotBlank    private String productCode;    /**     * 数据范围（1：全部数据权限 2:本租户数据权限 3：本部门数据权限 4：本部门及以下数据权限 5：本项目数据权限）     */    private Integer dataScope;    private List<Long> permissionIds;    public Role toEvent(String userCode, String tenantCode) {        Role role = new Role();        BeanUtils.copyProperties(this, role);        role.setTenantCode(tenantCode);        role.setCreator(userCode);        role.setCreateTime(new Date());        role.setUpdator(userCode);        role.setUpdateTime(new Date());        return role;    }}