package com.cyber.authing.entity.request;import com.alibaba.fastjson.JSONArray;import com.cyber.authing.entity.domain.Role;import com.cyber.domain.entity.OperateEntity;import lombok.Data;import lombok.EqualsAndHashCode;import org.springframework.beans.BeanUtils;import java.util.Date;@EqualsAndHashCode(callSuper = true)@Datapublic class CreateRoleRequest extends OperateEntity {		/**角色编码*/	private String code;	/**角色名称*/	private String name;	/**角色权限字符串*/	private JSONArray rolePermission;	/**显示顺序*/	private Integer roleSort;	/**部门编码*/	private String deptCode;	/**产品编码*/	private String productCode;		public Role toEvent(String userCode,String tenantCode) {		Role role = new Role();		BeanUtils.copyProperties(this, role);        role.setTenantCode(tenantCode);        role.setCreator(userCode);		role.setCreateTime(new Date());        role.setUpdator(userCode);		role.setUpdateTime(new Date());		return role;	}}