package com.cyber.authing.entity.request;import com.cyber.authing.entity.domain.User;import com.cyber.domain.entity.OperateEntity;import com.cyber.infrastructure.toolkit.UUIDs;import lombok.Data;import lombok.EqualsAndHashCode;import org.springframework.beans.BeanUtils;import javax.validation.constraints.Max;import javax.validation.constraints.Min;import javax.validation.constraints.NotBlank;import java.util.Date;@EqualsAndHashCode(callSuper = true)@Datapublic class CreateUserRequest extends OperateEntity {    /**     * 用户名称     */    private String name;    /**     * 用户昵称     */    private String nickname;    /**     * 用户密码     */    @NotBlank(message = "密码不能为空")    private String password;    /**     * 用户类型（0系统用户）     */    private Integer type;    /**     * 用户邮箱     */    private String email;    /**     * 手机号码     */    private String phoneNumber;    /**     * 用户性别（0男 1女 2未知）     */    private Integer sex;    /**     * 头像地址     */    private String avatar;    /**     * 租户信息     */    private String tenantCode;    /**     * 注册类型 1：手机注册 2：邮箱注册 3：账号注册     */    @Min(value = 1, message = "请选择正确的注册类型")    @Max(value = 3, message = "请选择正确的注册类型")    private Integer registerType;    public User toEvent(String userCode, String tenantCode) {        User user = new User();        BeanUtils.copyProperties(this, user);        user.setCreator(userCode);        user.setCreateTime(new Date());        user.setUpdator(userCode);        user.setUpdateTime(new Date());        user.setId(UUIDs.generateShortUuid());        user.setTenantCode(tenantCode);        return user;    }}