package com.cyber.authing.entity.request;import com.cyber.authing.entity.domain.DeptUser;import com.cyber.domain.entity.PagingRequest;import lombok.Data;import lombok.EqualsAndHashCode;import org.springframework.beans.BeanUtils;@EqualsAndHashCode(callSuper = true)@Datapublic class DeptUserRequest extends PagingRequest {    /**     * 用户ID     */    private Long userId;    /**     * 部门ID     */    private Long deptId;    /**     * 岗位ID     */    private Long positionId;    public DeptUser toEvent(String tenantCode) {        DeptUser deptUser = new DeptUser();        BeanUtils.copyProperties(this, deptUser);        deptUser.setTenantCode(tenantCode);        return deptUser;    }}