package com.cyber.authing.entity.request;import com.cyber.authing.entity.domain.Permission;import com.cyber.domain.entity.PagingRequest;import lombok.Data;import lombok.EqualsAndHashCode;import org.springframework.beans.BeanUtils;@EqualsAndHashCode(callSuper = true)@Datapublic class PermissionRequest extends PagingRequest {    /**     * 权限名称     */    private String name;    /**     * 路由地址     */    private String path;    /**     * 权限类型（1菜单 2接口）     */    private Integer type;    /**     * 接口数据范围（1：全部数据权限 2：本部门数据权限 3：本部门及以下数据权限 4：自定义数据权限）     */    private Integer dataScope;    /**     * 产品编码     */    private String productCode;    public Permission toEvent(String tenantCode) {        Permission permission = new Permission();        BeanUtils.copyProperties(this, permission);        permission.setTenantCode(tenantCode);        return permission;    }}