package com.cyber.authing.entity.request;import com.alibaba.fastjson.JSONArray;import com.cyber.authing.entity.domain.Product;import com.cyber.domain.entity.PagingRequest;import lombok.Data;import lombok.EqualsAndHashCode;import org.springframework.beans.BeanUtils;@EqualsAndHashCode(callSuper = true)@Datapublic class ProductRequest extends PagingRequest {    /**     * 产品编码     */    private String code;    /**     * 产品名称     */    private String name;    /**     * 产品负责人     */    private JSONArray managerId;    /**     * 产品描述     */    private String description;    public Product toEvent(String tenantCode) {        Product product = new Product();        BeanUtils.copyProperties(this, product);        product.setTenantCode(tenantCode);        return product;    }}