package com.cyber.authing.entity.request;import com.alibaba.fastjson.JSONArray;import com.cyber.authing.entity.domain.Project;import com.cyber.domain.entity.PagingRequest;import lombok.Data;import lombok.EqualsAndHashCode;import org.springframework.beans.BeanUtils;@EqualsAndHashCode(callSuper = true)@Datapublic class ProjectRequest extends PagingRequest {    /**     * 项目编码     */    private String code;    /**     * 项目名称     */    private String name;    /**     * 项目负责人     */    private JSONArray managerId;    /**     * 项目描述     */    private String description;    public Project toEvent(String tenantCode) {        Project project = new Project();        BeanUtils.copyProperties(this, project);        project.setTenantCode(tenantCode);        return project;    }}