package com.cyber.authing.entity.request;import com.cyber.authing.entity.domain.ProjectUser;import com.cyber.domain.entity.PagingRequest;import lombok.Data;import lombok.EqualsAndHashCode;import org.springframework.beans.BeanUtils;@EqualsAndHashCode(callSuper = true)@Datapublic class ProjectUserRequest extends PagingRequest {    /**     * 项目ID     */    private Long projectId;    /**     * 用户ID     */    private Long userId;    /**     * 角色     */    private Long roleId;    public ProjectUser toEvent(String tenantCode) {        ProjectUser projectUser = new ProjectUser();        BeanUtils.copyProperties(this, projectUser);        projectUser.setTenantCode(tenantCode);        return projectUser;    }}