package com.cyber.authing.entity.request;import com.alibaba.fastjson.JSONArray;import com.cyber.authing.entity.domain.Role;import com.cyber.domain.entity.PagingRequest;import lombok.Data;import lombok.EqualsAndHashCode;import org.springframework.beans.BeanUtils;@EqualsAndHashCode(callSuper = true)@Datapublic class RoleRequest extends PagingRequest {    /**     * 角色编码     */    private String code;    /**     * 角色名称     */    private String name;    /**     * 角色权限字符串     */    private JSONArray rolePermission;    /**     * 显示顺序     */    private Integer roleSort;    /**     * 部门编码     */    private String deptCode;    /**     * 产品编码     */    private String productCode;    public Role toEvent(String tenantCode) {        Role role = new Role();        BeanUtils.copyProperties(this, role);        role.setTenantCode(tenantCode);        return role;    }}