package com.cyber.authing.entity.request;import com.cyber.authing.entity.domain.Account;import com.cyber.domain.entity.IdRequest;import lombok.Data;import lombok.EqualsAndHashCode;import org.springframework.beans.BeanUtils;import java.util.Date;@EqualsAndHashCode(callSuper = true)@Datapublic class UpdateAccountRequest extends IdRequest {    /**     * 用户ID     */    private Long userId;    /**     * 登录账号     */    private String account;    /**     * 密码，系统登录必选，第三方登录可空     */    private String password;    /**     * 账号类型 0 系统账号 1 微信     */    private Integer type;    public Account toEvent(String userCode, String tenantCode) {        Account account = new Account();        BeanUtils.copyProperties(this, account);        account.setTenantCode(tenantCode);        account.setUpdator(userCode);        account.setUpdateTime(new Date());        return account;    }}