package com.cyber.authing.entity.request;import com.cyber.authing.entity.domain.DeptUser;import com.cyber.domain.entity.IdRequest;import lombok.Data;import lombok.EqualsAndHashCode;import org.springframework.beans.BeanUtils;import java.util.Date;@EqualsAndHashCode(callSuper = true)@Datapublic class UpdateDeptUserRequest extends IdRequest {    /**     * 用户ID     */    private Long userId;    /**     * 部门ID     */    private Long deptId;    /**     * 岗位ID     */    private Long positionId;    public DeptUser toEvent(String userCode, String tenantCode) {        DeptUser deptUser = new DeptUser();        BeanUtils.copyProperties(this, deptUser);        deptUser.setTenantCode(tenantCode);        deptUser.setUpdator(userCode);        deptUser.setUpdateTime(new Date());        return deptUser;    }}