package com.cyber.authing.entity.request;import com.cyber.authing.entity.domain.Position;import com.cyber.domain.entity.IdRequest;import lombok.Data;import lombok.EqualsAndHashCode;import org.springframework.beans.BeanUtils;import java.util.Date;@EqualsAndHashCode(callSuper = true)@Datapublic class UpdatePositionRequest extends IdRequest {    /**     * 岗位编码     */    private String code;    /**     * 岗位名称     */    private String name;    /**     * 显示顺序     */    private Integer orderNum;    /**     * 部门ID     */    private Long deptId;    /**     * 岗位描述     */    private String description;    public Position toEvent(String userCode, String tenantCode) {        Position position = new Position();        BeanUtils.copyProperties(this, position);        position.setTenantCode(tenantCode);        position.setUpdator(userCode);        position.setUpdateTime(new Date());        return position;    }}