package com.cyber.authing.entity.request;import com.alibaba.fastjson.JSONArray;import com.cyber.authing.entity.domain.Product;import com.cyber.domain.entity.IdRequest;import lombok.Data;import lombok.EqualsAndHashCode;import org.springframework.beans.BeanUtils;import java.util.Date;@EqualsAndHashCode(callSuper = true)@Datapublic class UpdateProductRequest extends IdRequest {    /**     * 产品编码     */    private String code;    /**     * 产品名称     */    private String name;    /**     * 产品负责人     */    private JSONArray managerId;    /**     * 产品描述     */    private String description;    public Product toEvent(String userCode, String tenantCode) {        Product product = new Product();        BeanUtils.copyProperties(this, product);        product.setTenantCode(tenantCode);        product.setUpdator(userCode);        product.setUpdateTime(new Date());        return product;    }}