package com.cyber.authing.entity.request;import com.cyber.authing.entity.domain.ProjectUser;import com.cyber.domain.entity.IdRequest;import lombok.Data;import lombok.EqualsAndHashCode;import org.springframework.beans.BeanUtils;import javax.validation.constraints.NotNull;import java.util.Date;@EqualsAndHashCode(callSuper = true)@Datapublic class UpdateProjectUserRequest extends IdRequest {    /**     * 角色     */    @NotNull    private Long roleId;    public ProjectUser toEvent(String userCode, String tenantCode) {        ProjectUser projectUser = new ProjectUser();        BeanUtils.copyProperties(this, projectUser);        projectUser.setTenantCode(tenantCode);        projectUser.setUpdator(userCode);        projectUser.setUpdateTime(new Date());        return projectUser;    }}