package com.cyber.authing.entity.request;import com.alibaba.fastjson.JSONArray;import com.cyber.authing.entity.domain.Tenant;import com.cyber.domain.entity.IdRequest;import lombok.Data;import lombok.EqualsAndHashCode;import org.springframework.beans.BeanUtils;import java.util.Date;@EqualsAndHashCode(callSuper = true)@Datapublic class UpdateTenantRequest extends IdRequest {    /**     * 租户编码     */    private String code;    /**     * 租户名称     */    private String name;    /**     * 租户管理员ID     */    private JSONArray managerId;    /**     * 租户描述     */    private String description;    public Tenant toEvent(String userCode, String tenantCode) {        Tenant tenant = new Tenant();        BeanUtils.copyProperties(this, tenant);        tenant.setTenantCode(tenantCode);        tenant.setUpdator(userCode);        tenant.setUpdateTime(new Date());        return tenant;    }}